import React, { Component } from 'react';
import {
    View,
    Text
} from 'react-native';

class Time extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <Text style={this.props.customStyles}>
                {this.props.value}
            </Text>
        );
    }
}

export default Time;